function createRequest(value,action){return data={value,action}}function errorShow(error){$("div.admin-form-error").css("visibility","visible"),$("div.admin-form-error").html(error)}function showContent(){$("div.admin-panel-form").css("display","none"),$("div.entered-phone-numbers").css("display","flex"),$.ajax({url,type:"POST",dataType:"json",data:createRequest(void 0,"show-numbers"),success:function(answer){answer.length?($("section.admin-panel").html(generateContent(answer)),$("div.entered-phone-numbers").css("display","flex")):$("div.entered-phone-numbers").html("<div class='phone-number-item'><div class='number'>Номерів поки немає</div></div>")}})}function generateContent(array){let content=``;for(let i=0;i<array.length;i++)content+=`\n                <div class="entered-phone-numbers">\n                <div class="phone-number-item">\n                <div class="number">${array[i]}</div>\n                <div class="close" onclick="deleteItem('${array[i]}')">x</div>\n                </div>\n                </div>\n        `;return content}function deleteItem(item){confirm(`Видалити ${item} ?`)&&$.ajax({url,type:"POST",dataType:"json",data:createRequest(item,"deleteItem"),success:function(answer){console.log(answer),showContent()}})}let url="http://www.vdodovich.h1n.ru/server/request.php";$("button.admin-password-button").on("click",function(){let password=$("input.admin-password-input").val();if(!password.length)return errorShow("Ви не ввели пароль"),!1;console.log("Request about sign."),$.ajax({url,type:"POST",dataType:"json",data:createRequest(password,"sign"),success:function(answer){answer?(console.log("Sign success"),showContent()):(errorShow("Невірний пароль"),console.log("Invalid password. Try again!"))},error:function(){errorShow("Невдалось приєднатись до сервера")}})});